<body class="hold-transition login-page">    <div class="login-box">      <div style="background-color:#D2D6DE;"  class="login-logo">      <!--  <a style="color:#00C" href="#">WelCome to<br><b style="color:#FFF">Garage Back Office System</b></a> -->      </div>                  <div  class="login-box-body">        <p class="login-box-msg">Sign in to start your session</p>        <form action="../../index2.html" id="enter" method="post">          <div class="form-group has-feedback">            <input id="user_name" type="email" class="form-control" placeholder="User Name">            <span class="glyphicon glyphicon-envelope form-control-feedback"></span>          </div>          <div class="form-group has-feedback">            <input id="password" type="password" class="form-control" placeholder="Password">            <span class="glyphicon glyphicon-lock form-control-feedback"></span>          </div>          <div class="row">            <div class="col-xs-8">                          </div><!-- /.col -->            <div class="col-xs-4">              <button id="loging_btn" type="button" class="btn btn-primary btn-block btn-flat">Sign In</button>                        </div><!-- /.col -->                      </div>        </form>      </div>    </div>   </body><script>document.addEventListener('keyup', doc_keyUp, false);function doc_keyUp(e) {     if (e.keyCode == 13 ) {					var username=$('#user_name').val();    	var password=$('#password').val();				   		if(username==""){		$('#user_name').focus();		}else if(password==""){		$('#password').focus();		}else{				   	$.ajax({            url: '<?php echo base_url() ?>index.php/sys_log/dashboard/user_log',            method: 'POST',            data: {username:username,password:password},           	success: function(data) {							if (data==0) {			   					alert("Passowrd or email invalid");	            	}else{			   		jd = $.parseJSON(data)                   	if (jd.retval)                    $(location).attr('href', jd.url); 				}            },           	error: function(err, message, xx) {            }    		});			}				}} 	$('#loging_btn').click(function(e) {   		var username=$('#user_name').val();    	var password=$('#password').val();				   		if(username==""){		$('#user_name').focus();		}else if(password==""){		$('#password').focus();		}else{				   	$.ajax({            url: '<?php echo base_url() ?>index.php/sys_log/dashboard/user_log',            method: 'POST',            data: {username:username,password:password},           	success: function(data) {							if (data==0) {			   					alert("Passowrd or email invalid");	            	}else{			   		jd = $.parseJSON(data)                   	if (jd.retval)                    $(location).attr('href', jd.url); 				}            },           	error: function(err, message, xx) {            }    		});			}	});  </script>    <script>      $(function () {        $('input').iCheck({          checkboxClass: 'icheckbox_square-blue',          radioClass: 'iradio_square-blue',          increaseArea: '20%' // optional        });      });    </script>  </body>